document.getElementById('templateForm').addEventListener('submit', function(e) {
	e.preventDefault();

	// Validasi minimal
	const namaTemplate = this.querySelector('[name="nama_template"]').value.trim();
	const tipeSurat = this.querySelector('[name="tipe_surat"]').value.trim();
	let konten = '';

	// Ambil konten dari CKEditor dengan pengecekan
	if (CKEDITOR.instances['konten']) {
		konten = CKEDITOR.instances['konten'].getData().trim();
	}

	if (!namaTemplate || !tipeSurat || !konten) {
		alert("Nama template, tipe surat, dan konten harus diisi!");
		return;
	}

	// Submit form langsung tanpa AJAX untuk menghindari masalah
	this.submit();
});

